import React, { useEffect, useState } from "react";
import { ParallaxBanner } from "react-scroll-parallax";
import { useParallax } from "react-scroll-parallax";
import "./Home.css";
import { Link } from "react-router-dom";

const Home = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 480);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 480);
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const parallax1 = isMobile ? {} : useParallax({ rotate: [-70, 10], translateX: [250, 100] });
  const parallax2 = isMobile ? {} : useParallax({ rotate: [90, 20], translateX: [-130, 0] });
  const parallax3 = isMobile ? {} : useParallax({ rotate: [-90, 20], translateY: [-70, 250] });
  const parallax4 = isMobile ? {} : useParallax({ rotate: [90, -20], translateY: [70, -250] });
  const parallax5 = isMobile ? {} : useParallax({ rotate: [60, -20], translateY: [-70, 290] });

  return (
    <section className="homebody">
      <div className="header">PORTFOLIO Â© 2025</div>
      <div className="container-body">
        <div className="imageposition">
          <img src="/images/1.jpg" alt="" className="myimage" />
        </div>
        <ParallaxBanner layers={[headline1, headline2]} className="bg-container" />
      </div>
      <div className="myintro">
        <span className="myintro1 sparkle-text">
          Welcome to the digital domain of Harshin Mansoor, a passionate MERN Stack developer and recent BTech graduate. Explore my portfolio to see my work and potential in action.
        </span>
      </div>
      <div className="workidone">
        <span className="workdonetitle">Projects I Have Done</span>
        <Link to={"https://github.com/harshinJJ/Blood-Bank-project"} target="_blank" style={{ textDecoration: "none" }}>
          <div ref={parallax1.ref} className="workdonebubble1">Healthcare Platform</div>
        </Link>
        <div ref={parallax2.ref} className="workdonebubble2">Personal Portfolio</div>
        <Link to={"https://github.com/harshinJJ/Blood-Bank-project"} target="_blank" style={{ textDecoration: "none" }}>
          <div ref={parallax3.ref} className="workdonebubble3">Blood Donor Project</div>
        </Link>
        <Link to={"https://weather-searching-app.vercel.app/"} target="_blank" style={{ textDecoration: "none" }}>
          <div ref={parallax4.ref} className="workdonebubble4">Weather Searching App</div>
        </Link>
        <Link to={"https://skill-swap-flax.vercel.app/"} target="_blank" style={{ textDecoration: "none" }}>
          <div ref={parallax5.ref} className="workdonebubble5">Skill Sharing Website</div>
        </Link>
      </div>
      <br />
      <br />
      <br />
      <div className="cvpart">
        <img src="/images/5.jpg" alt="" className="cvimage" />
      </div>
    </section>
  );
};

export default Home;
