import React, { useEffect } from "react";
import "./StarsBackground.css";

const StarsBackground = () => {
  useEffect(() => {
    console.log("StarsBackground Mounted!"); // Debugging

    const numStars = 100;
    const starsContainer = document.getElementById("stars");

    if (!starsContainer) {
      console.error("❌ Element with id 'stars' not found!");
      return;
    }

    // Clear existing stars before adding new ones
    starsContainer.innerHTML = "";

    for (let i = 0; i < numStars; i++) {
      let star = document.createElement("div");
      star.className = "star";

      let x = Math.random() * window.innerWidth;
      let y = Math.random() * window.innerHeight;
      let duration = Math.random() * 5 + 5; // Random duration between 5-10s

      star.style.left = `${x}px`;
      star.style.top = `${y}px`;
      star.style.animationDuration = `${duration}s`;

      starsContainer.appendChild(star);
    }
  }, []);

  return <div id="stars"></div>;
};

export default StarsBackground;
