import React, { useEffect } from "react";
import "./StarsBackground.css";

const StarsBackground = () => {
  useEffect(() => {
    console.log("üåü StarsBackground Mounted!");

    const numStars = 100;
    const starsContainer = document.getElementById("stars");

    if (!starsContainer) {
      console.error("‚ùå Element with id 'stars' not found!");
      return;
    }

    starsContainer.innerHTML = ""; // Clear any existing stars

    for (let i = 0; i < numStars; i++) {
      let star = document.createElement("div");
      star.className = "star";

      let x = Math.random() * window.innerWidth;  // Random X position
      let y = Math.random() * window.innerHeight; // Random Y position
      let duration = Math.random() * 5 + 5; // 5-10s random duration

      star.style.left = `${x}px`;
      star.style.top = `${y}px`;
      star.style.animationDuration = `${duration}s`;

      starsContainer.appendChild(star);

      console.log(`‚≠ê Added star ${i + 1} at position (${x}, ${y})`);
    }
  }, []);

  return <div id="stars"></div>;
};

export default StarsBackground;
